import React, { useState, useEffect } from "react";
import AccessToken from "./fetch/AccessToken";
import Header from "../components/Header";

export default function SpotifyFetch() {
  const [trackDetails, setTrackDetails] = useState(null);
  const [accessToken, setAccessToken] = useState(null); // Initialize accessToken state

  useEffect(() => {
    const fetchAccessToken = AccessToken();

    const fetchTrackDetails = async () => {
      try {
        const trackId = "5VC29kHMkzcaorzPKUqJbl";
        const response = await fetch(
          `https://api.spotify.com/v1/tracks/${trackId}`,
          {
            method: "GET",
            headers: {
              Authorization: `Bearer ${accessToken}`,
            },
          },
        );

        if (!response.ok) {
          throw new Error("Failed to fetch track details");
        }

        const data = await response.json();
        setTrackDetails(data);
      } catch (error) {
        console.error("Error fetching track details:", error);
      }
    };

    if (accessToken) {
      fetchTrackDetails();
    } else {
      fetchAccessToken();
    }
  }, [accessToken]); // Include accessToken as a dependency to trigger the effect when it changes

  return "";
}
