import React, { useState, useEffect } from "react";
import ArtistInfo from "./ArtistInfo";
import spotifyAccessToken from "../tokens/SpotifyAccessToken";
import Auth from "./Auth";
import AlbumsList from "./AlbumsList"; // Import AlbumsList component

const SpotifyData = () => {
  const accessToken = <Auth />;

  const [spotifyData, setSpotifyData] = useState(null);

  useEffect(() => {
    const apiUrl = "https://api.spotify.com/v1/artists/53XhwfbYqKCa1cC15pYq2q";

    const fetchConfig = {
      method: "GET",
      headers: {
        Authorization: `Bearer ${accessToken}`,
      },
    };

    fetch(apiUrl, fetchConfig)
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        setSpotifyData(data);
      })
      .catch((error) => {
        console.error("There was a problem with the fetch operation:", error);
      });
  }, [accessToken]);

  return (
    <div>
      <h1>Spotify Data</h1>
      {spotifyData && (
        <>
          <ArtistInfo artistData={spotifyData} />
          <hr />
          <AlbumsList albums={spotifyData.items} /> {/* Pass albums directly */}
        </>
      )}
    </div>
  );
};

export default SpotifyData;
