import React, { useState } from "react";
import Auth from "./Auth";

const SpotifySingleInfo = () => {
  const [singleId, setSingleId] = useState("");
  const [singleInfo, setSingleInfo] = useState(null);
  const [error, setError] = useState(null);

  const handleInputChange = (event) => {
    setSingleId(event.target.value);
  };

  const fetchSingleInfo = (accessToken) => {
    if (!singleId) {
      setError("Please enter a Spotify single ID.");
      return;
    }

    const apiUrl = `https://api.spotify.com/v1/tracks/${singleId}`;

    const fetchConfig = {
      headers: {
        Authorization: `Bearer ${accessToken}`,
      },
    };

    fetch(apiUrl, fetchConfig)
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        setSingleInfo(data);
        setError(null);
      })
      .catch((error) => {
        console.error("There was a problem with the fetch operation:", error);
        setError(error.message);
      });
  };

  return (
    <div>
      <h1>Spotify Single Info</h1>
      <input
        type="text"
        placeholder="Enter Spotify Single ID"
        value={singleId}
        onChange={handleInputChange}
      />
      <button onClick={() => fetchSingleInfo(<Auth />)}>Get Single Info</button>
      {error && <p>Error: {error}</p>}
      {singleInfo && (
        <div>
          <h2>Title: {singleInfo.name}</h2>
          {/* Display other track info here */}
        </div>
      )}
    </div>
  );
};

export default SpotifySingleInfo;
