import React, { useState, useEffect } from "react";
import AccessToken from "./fetch/AccessToken";

export default function SpotifyFetch({ trackId }) {
  const [trackDetails, setTrackDetails] = useState(null);
  const fetchAccessToken = AccessToken(); // Använd AccessToken för att hämta access token

  useEffect(() => {
    const fetchTrackDetails = async () => {
      try {
        const accessToken = await fetchAccessToken(); // Hämta access token genom att anropa fetchAccessToken
        const response = await fetch(
          `https://api.spotify.com/v1/tracks/${trackId}`,
          {
            method: "GET",
            headers: {
              Authorization: `Bearer ${accessToken}`,
            },
          },
        );

        if (!response.ok) {
          throw new Error("Failed to fetch track details");
        }

        const data = await response.json();
        setTrackDetails(data);
      } catch (error) {
        console.error("Error fetching track details:", error);
      }
    };

    fetchTrackDetails();
  }, [fetchAccessToken, trackId]); // Include fetchAccessToken as a dependency

  return (
    <>
      {trackDetails && (
        <div>
          <h3>Track Details</h3>
          <p>Title: {trackDetails.name}</p>
          <p>Artist: {trackDetails.artists[0].name}</p>
          <p>Album: {trackDetails.album.name}</p>
          {/* Display the access token */}
          {/* Show other details about the track if needed */}
        </div>
      )}
    </>
  );
}
