import React, { useState, useEffect } from "react";
import ArtistInfo from "./ArtistInfo";
import spotifyAccessToken from "../tokens/SpotifyAccessToken";

const SpotifyData = () => {
  const [accessToken, setAccessToken] = useState(null);
  const [spotifyData, setSpotifyData] = useState(null);
  const [error, setError] = useState(null); // Add state for errors

  useEffect(() => {
    const generateAccessToken = async () => {
      const clientId = "2996a4789f6c4508a187d42e86df6eac";
      const clientSecret = "eae403656d5d4b4cb324745279a224e1";

      const response = await fetch("https://accounts.spotify.com/api/token", {
        method: "POST",
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
        },
        body: new URLSearchParams({
          client_id: clientId,
          client_secret: clientSecret,
          grant_type: "client_credentials",
        }),
      });
    };

    generateAccessToken();
  }, []);

  useEffect(() => {
    if (accessToken) {
      const apiUrl =
        "https://api.spotify.com/v1/artists/53XhwfbYqKCa1cC15pYq2q";

      const fetchConfig = {
        method: "GET",
        headers: {
          Authorization: `Bearer ${accessToken}`,
        },
      };

      fetch(apiUrl, fetchConfig)
        .then((response) => {
          if (!response.ok) {
            throw new Error("Network response was not ok");
          }
          return response.json();
        })
        .then((data) => {
          setSpotifyData(data);
        })
        .catch((error) => {
          console.error("There was a problem with the fetch operation:", error);
          setError(error.message); // Set error message
        });
    }
  }, [accessToken]);

  return (
    <div>
      <h1>Spotify Data</h1>
      {error && <p>Error: {error}</p>}
      {spotifyData && (
        <>
          <ArtistInfo artistData={spotifyData} />
          <hr />
          <h2>{spotifyData.name} - Albums</h2>
          {spotifyData.items && ( // Check for presence of items before rendering AlbumsList
            <div className="albums-container">
              {spotifyData.items.map((album) => (
                <div key={album.id} className="album-item">
                  {/* ... Album details ... */}
                </div>
              ))}
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default SpotifyData;
